import "../css/VolunteerLogin.css";
import { useNavigate } from "react-router-dom";
import React, { useState, useEffect } from "react";
import axios from "axios";

const VolunteerLogin = () => {
  const [id, setId] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [volunteer_login, setVolunteers] = useState([]);
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    switch (name) {
      case "email":
        setEmail(value);
        break;
      case "password":
        setPassword(value);
        break;
      default:
        break;
    }
  };

  const validateForm = () => {
    let errors = {};
    let isValid = true;

    const passRegex =
      /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*])(?=.{8,})/;

    const emailRegex = /\S+@\S+\.\S+/;

    if (!email) {
      errors.email = "Email is required";
      isValid = false;
    } else if (!emailRegex.test(email)) {
      errors.email = "Email is invalid";
      isValid = false;
    }

    if (!password) {
      errors.password = "Password is required";
      isValid = false;
    } else if (!passRegex.test(password)) {
      errors.password =
        " Password must contain at least 8 characters, including one uppercase letter, one lowercase letter, one number, and one special character";
      isValid = false;
    }

    setErrors(errors);
    return isValid;
  };

  useEffect(() => {
    (async () => await Load())();
  }, []);

  async function Load() {
    const result = await axios.get("http://localhost:8080/get");
    setVolunteers(result.data);
    console.log(result.data);
  }

  async function save(event) {
    event.preventDefault();
    if (validateForm()) {
      try {
        await axios.post("http://localhost:8080/log_in", {
          Email: email,
          Password: password,
      },{
        withCredentials: true, // Include credentials
        headers: {
            'Content-Type': 'application/json',
        },
        });
        alert("Volunteer registation has been done successfully");
        setId("");

        setEmail("");
        setPassword("");
      } catch (err) {
        alert("Volunteer Registation Failed");
      }
      navigate("/volunteerProfile");
    }
  }

  return (
    <div className="main-login">
      <form className="myform">
        <h1>Login</h1>
        <div class="form-group">
          <label for="exampleInputEmail1" className="label-control">
            Email Address
          </label>
          <input
            type="email"
            class="form-control"
            aria-describedby="emailHelp"
            placeholder="Enter Email"
            required
            id="email"
            value={email}
            onChange={handleChange}
            name="email"
          />
          {errors.email && (
            <div className="error" style={{ color: "red" }}>
              {errors.email}
            </div>
          )}

          <small id="emailHelp" class="form-text text-muted">
            We'll never share your email with anyone else.
          </small>
        </div>
        <div class="form-group">
          <label for="exampleInputPassword1" className="label-control">
            Password
          </label>
          <input
            type="password"
            class="form-control"
            name="password"
            placeholder="Password"
            id="password"
            value={password}
            onChange={handleChange}
            required
          />
          {errors.password && (
            <div className="error" style={{ color: "red" }}>
              {errors.password}
            </div>
          )}
        </div>
        <div className="text-center">
          <button type="submit" class="btn btn-primary " onClick={save}>
            Login
          </button>
        </div>

        <p>
          <a href="/volunteerRegister">Don't have an account?</a>
        </p>
      </form>
    </div>
  );
};

export default VolunteerLogin;
